import React, { Component, createRef, forwardRef } from "react";
import clone from "clone";
import { Tag, TagValue } from "@/registry/new-york/tag-search-box/Tag";
import { TagInput } from "@/registry/new-york/tag-search-box/TagInput";
import { mergeRefs } from "@/registry/new-york/tag-search-box/utils/MergeRefs";
import { TagSearchBoxContext } from "@/registry/new-york/tag-search-box/TagSearchboxContext";
import { withOutsideClick } from "@/registry/new-york/tag-search-box/utils/withOutsideClick";

import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import { X, Info, Search } from "lucide-react";
import { cn } from "@/lib/utils";
import { AttributeValue } from "@/registry/new-york/tag-search-box/AttributeSelect";

export type { AttributeValue };

/**
 * ÁÑ¶ÁÇπÊâÄÂú®‰ΩçÁΩÆÁ±ªÂûã
 */
export enum FocusPosType {
  INPUT = 0,
  INPUT_EDIT = 1,
  TAG = 2,
}

let COUNTER = 0;

interface TagSearchBoxProps {
  /**
   * Ë¶ÅÈÄâÊã©ËøáÊª§ÁöÑËµÑÊ∫êÂ±ûÊÄßÁöÑÈõÜÂêà
   */
  attributes?: AttributeValue[];
  /**
   * ÊêúÁ¥¢Ê°Ü‰∏≠ÈªòËÆ§ÂåÖÂê´ÁöÑÊ†áÁ≠æÂÄºÁöÑÈõÜÂêà
   */
  defaultValue?: TagValue[];
  /**
   * ÈÖçÂêà onChange ‰Ωú‰∏∫ÂèóÊéßÁªÑ‰ª∂‰ΩøÁî®
   */
  value?: TagValue[];
  /**
   * ÂΩìÊñ∞Â¢û/‰øÆÊîπ/ÂáèÂ∞ëÊ†áÁ≠æÊó∂Ë∞ÉÁî®Ê≠§ÂáΩÊï∞
   *
   * **üí° Áî®‰∫éËß¶ÂèëÊêúÁ¥¢**
   */
  onChange?: (tags: TagValue[]) => void;
  /**
   * ÊêúÁ¥¢Ê°ÜÊî∂Ëµ∑ÂêéÂÆΩÂ∫¶
   * @default 210
   */
  minWidth?: string | number;
  /**
   * ÊòØÂê¶Á¶ÅÁî®
   * @default false
   * @since 2.4.1
   */
  disabled?: boolean;
  /**
   * ÊêúÁ¥¢Ê°Ü‰∏≠ÊèêÁ§∫ËØ≠
   *
   * @default "Â§ö‰∏™ÂÖ≥ÈîÆÂ≠óÁî®Á´ñÁ∫ø "|" ÂàÜÈöîÔºåÂ§ö‰∏™ËøáÊª§Ê†áÁ≠æÁî®ÂõûËΩ¶ÈîÆÂàÜÈöî" ÔºàÂ∑≤Â§ÑÁêÜÂõΩÈôÖÂåñÔºâ
   */
  tips?: string;
  /**
   * ËµÑÊ∫êÂ±ûÊÄßÈÄâÊã©‰∏ãÊãâÊ°ÜÊèêÁ§∫
   *
   * @default "ÈÄâÊã©ËµÑÊ∫êÂ±ûÊÄßËøõË°åËøáÊª§" ÔºàÂ∑≤Â§ÑÁêÜÂõΩÈôÖÂåñÔºâ
   */
  attributesSelectTips?: string;
  /**
   * ÈöêËóèÂ∏ÆÂä©ÊåâÈíÆ
   *
   * @default false
   */
  hideHelp?: boolean;
  /**
   * Ê∏ÖÁ©∫ÊåâÈíÆÁÇπÂáªÂõûË∞É
   *
   * @since 2.2.2
   */
  onClearButtonClick?: (e: React.MouseEvent) => void;
  /**
   * Â∏ÆÂä©ÊåâÈíÆÁÇπÂáªÂõûË∞É
   *
   * ËøîÂõû `false` ÈòªÊ≠¢ÈªòËÆ§ÊèêÁ§∫Ë°å‰∏∫
   *
   * @since 2.2.2
   */
  onHelpButtonClick?: (e: React.MouseEvent) => void | false;
  /**
   * ÊêúÁ¥¢ÊåâÈíÆÁÇπÂáªÂõûË∞É
   *
   * @since 2.2.2
   */
  onSearchButtonClick?: (e: React.MouseEvent, value: TagValue[]) => void;
  /**
   * Á¶ÅÁî®Ê†πÊçÆËæìÂÖ•ÂÄºËøáÊª§ËµÑÊ∫êÂ±ûÊÄßÈÄâÈ°π
   *
   * **Êñ∞Â¢ûÊàñ‰øÆÊîπÊ†áÁ≠æÊó∂Â∞ÜÂ±ïÁ§∫ÂÖ®ÈÉ®ËµÑÊ∫êÂ±ûÊÄß**
   *
   * @since 2.4.0
   * @default false
   */
  disableAttributesFilter?: boolean;
  /**
   * Âà†Èô§Âçï‰∏™Ê†áÁ≠æÁöÑÂõûË∞É
   *
   * ËøîÂõû `false` ÈòªÊ≠¢Âà†Èô§
   *
   * @since 2.7.4
   */
  onDeleteTag?: (tag: TagValue) => Promise<boolean> | boolean;
}

interface TagSearchBoxState {
  active: boolean;
  curPos: number;
  curPosType: FocusPosType;
  showSelect: boolean;
  tags: TagValue[];
  lastValue?: TagValue[];
}

class ITagSearchBox extends Component<
  TagSearchBoxProps & {
    forwardRef?: React.Ref<HTMLDivElement>;
  },
  TagSearchBoxState
> {
  static displayName = "TagSearchBox";

  private searchWrapRef = createRef<HTMLDivElement>();
  private searchBoxRef = createRef<HTMLDivElement>();
  private tagRefs: { [key: string]: any } = {};

  constructor(props: TagSearchBoxProps) {
    super(props);
    this.tagRefs = {};

    const { defaultValue = [], value } = props;
    const initialTags = defaultValue.map((item) => {
      const newItem = clone(item);
      // @ts-ignore
      newItem["_key"] = COUNTER++;
      return newItem;
    });

    this.state = {
      active: false,
      curPos: 0,
      curPosType: FocusPosType.INPUT,
      showSelect: true,
      tags: initialTags,
      lastValue: value,
    };
  }

  componentDidUpdate(prevProps: TagSearchBoxProps) {
    const { value } = this.props;
    if (value !== prevProps.value) {
      const tagValue =
        value?.map((item) => {
          const newItem = clone(item);
          if (!("_key" in newItem)) {
            // @ts-ignore
            newItem["_key"] = COUNTER++;
          }
          return newItem;
        }) || [];

      this.setState({
        tags: clone(tagValue),
        lastValue: value,
      });
    }
  }

  // ÈáçÁΩÆÊ†áÁ≠æÁä∂ÊÄÅ
  private resetTagsState = (
    props: TagSearchBoxProps,
    callback?: () => void
  ) => {
    if ("value" in props) {
      const tagValue =
        props.value?.map((item) => {
          const newItem = clone(item);
          if (!("_key" in newItem)) {
            // @ts-ignore
            newItem["_key"] = COUNTER++;
          }
          return newItem;
        }) || [];

      this.setState({ tags: clone(tagValue) }, () => {
        if (callback) setTimeout(callback, 0);
      });
    }
  };

  // ÊâìÂºÄÊêúÁ¥¢Ê°Ü
  open = () => {
    const { disabled } = this.props;
    const { active, tags = [] } = this.state;

    if (disabled) {
      return;
    }

    if (!active) {
      this.setState({ active: true });
      this.setState({ curPosType: FocusPosType.INPUT, curPos: tags.length });
    } else {
      this.handleTagEvent("click-input", tags.length);
    }
    this.setState({ showSelect: true });

    setTimeout(() => {
      this.tagRefs[`tag-${tags.length}`]?.moveToEnd();
    }, 100);
  };

  // ÂÖ≥Èó≠ÊêúÁ¥¢Ê°Ü
  close = () => {
    const { active, tags } = this.state;
    const updatedTags = tags.map((item, index) => {
      const newItem = clone(item);
      // @ts-ignore
      if (newItem["_edit"]) {
        this.tagRefs[`tag-${index}`]?.editDone();
        // @ts-ignore
        newItem["_edit"] = false;
      }
      return newItem;
    });

    this.setTags(
      updatedTags,
      () => {
        this.setState({ showSelect: false }, () => {
          if (active) {
            this.setState({ active: false }, () => {
              this.setState({ curPos: -1 });
              if (this.searchBoxRef.current) {
                this.searchBoxRef.current.scrollLeft = 0;
              }
            });
          }
        });
      },
      false
    );
  };

  // Ëé∑ÂèñÊ†áÁ≠æÂÄº
  private getValue = (tags: TagValue[]) => {
    const result: TagValue[] = [];

    tags.forEach((item) => {
      const { values, attr = undefined } = item;
      if (values && values.length > 0) {
        result.push({
          attr,
          values,
          // @ts-ignore
          _key: item["_key"],
          // @ts-ignore
          _edit: item["_edit"],
        });
      }
    });

    return result;
  };

  // ÈÄöÁü•ÂèòÊõ¥
  private notify = (tags: TagValue[]) => {
    const { onChange = () => {} } = this.props;
    onChange(this.getValue(tags));
  };

  // ËÆæÁΩÆÊ†áÁ≠æÁä∂ÊÄÅ
  private setTags = (
    newTags: TagValue[],
    callback?: (() => void) | null,
    shouldNotify = true
  ) => {
    const { value } = this.props;
    const cb = () => {
      if (shouldNotify) {
        this.notifyChange(newTags);
      }
      if (callback) {
        callback();
      }
    };

    // ÂèóÊéßÊ®°Âºè
    if (shouldNotify && value) {
      this.resetTagsState(this.props, cb);
    } else {
      this.setState({ tags: newTags }, () => {
        setTimeout(cb, 0);
      });
    }
  };

  // ÈÄöÁü•Ê†áÁ≠æÂèòÊõ¥
  private notifyChange = (tags: TagValue[]) => {
    this.notify(tags);
  };

  // Â§ÑÁêÜÊ∏ÖÈô§ÊåâÈíÆÁÇπÂáª
  private handleClear = (e: React.MouseEvent) => {
    e.stopPropagation();
    const { onClearButtonClick = () => {} } = this.props;
    onClearButtonClick(e);

    const { tags } = this.state;
    const nextTags = tags.filter((i) => i.attr && i.attr.removeable === false);
    const index = `tag-${nextTags.length}`;

    if (tags.length <= 0) {
      this.tagRefs[index]?.setInputValue("");
      return;
    }

    this.setTags(nextTags, () => {
      return setTimeout(() => {
        this.tagRefs[index]?.setInputValue("");
        this.tagRefs[index]?.focusInput();
      }, 0);
    });

    this.setState({
      curPos: 0,
      curPosType: FocusPosType.INPUT,
    });

    // Âà∑Êñ∞‰∏ãÊãâÂàóË°®‰ΩçÁΩÆ
    const input = this.tagRefs[`tag-${tags.length}`];
    if (input) {
      input?.scheduleUpdate?.();
    }
  };

  // Â§ÑÁêÜÂ∏ÆÂä©ÊåâÈíÆÁÇπÂáª
  private handleHelp = (e: React.MouseEvent) => {
    e.stopPropagation();
    const { onHelpButtonClick = () => {} } = this.props;

    if (onHelpButtonClick(e) === false) {
      return;
    }
  };

  // Â§ÑÁêÜÊêúÁ¥¢ÊåâÈíÆÁÇπÂáª
  private handleSearch = (e: React.MouseEvent) => {
    const { active, tags } = this.state;
    const { onSearchButtonClick = () => {} } = this.props;

    if (!active) {
      // Â¶ÇÊûúÁõëÂê¨‰∫ÜÊåâÈíÆÁÇπÂáªÔºåÊ≠§Êó∂ÁÇπÂáªÊåâÈíÆ‰∏çÊøÄÊ¥ªÊêúÁ¥¢Ê°Ü
      if ("onSearchButtonClick" in this.props) {
        e.stopPropagation();
        onSearchButtonClick(e, this.getValue(tags));
      }
      return;
    }

    e.stopPropagation();

    // ËæìÂÖ•ÂÄºÁîüÊàêÊ†áÁ≠æÊìç‰Ωú‰ºöÂºÇÊ≠•ÊîπÂèò tags
    // Ê≠§Â§Ñ‰øùËØÅ tags Áä∂ÊÄÅÂèòÂåñÂÆåÊàêÂêéÂÜçËøõË°åÂõûË∞É
    setTimeout(() => {
      onSearchButtonClick(e, this.getValue(tags));
    }, 100);

    let flag = false;

    const input = this.tagRefs[`tag-${tags.length}`];
    if (input && input.addTagByInputValue) {
      if (input.addTagByInputValue()) {
        flag = true;
      }
    }

    for (let i = 0; i < tags.length; ++i) {
      const tagInput = this.tagRefs[`tag-${i}`];
      if (!tagInput || !tagInput.addTagByEditInputValue) return;

      // @ts-ignore
      if (tags[i]["_edit"] && tagInput.addTagByEditInputValue()) {
        flag = true;
      }
    }

    if (flag) return;

    this.notify(tags);
    input.focusInput();
  };

  // Â§ÑÁêÜÊ†áÁ≠æ‰∫ã‰ª∂
  private handleTagEvent = async (
    type: string,
    index: number,
    payload?: any
  ) => {
    const { tags } = this.state;
    const newTags = clone(tags);

    switch (type) {
      case "add":
        payload["_key"] = COUNTER++;
        newTags.splice(++index, 0, payload);
        this.setTags(newTags, () => {
          this.tagRefs[`tag-${index}`]?.focusInput();
        });
        this.setState({ showSelect: false });
        break;

      case "edit":
        this.tagRefs[`tag-${index}`]?.editDone();
        newTags[index].attr = payload.attr;
        newTags[index].values = payload.values;
        // @ts-ignore
        newTags[index]["_edit"] = false;
        this.setTags(newTags);
        index++;
        this.setState({
          showSelect: false,
          curPosType: FocusPosType.INPUT,
        });
        break;

      case "edit-cancel":
        this.tagRefs[`tag-${index}`]?.editDone();
        this.setTags(newTags, () => null, false);
        this.setState({
          showSelect: false,
          curPosType: FocusPosType.INPUT,
        });
        break;

      case "editing":
        if ("attr" in payload && newTags[index])
          newTags[index].attr = payload.attr;
        if ("values" in payload && newTags[index])
          newTags[index].values = payload.values;
        this.setTags(newTags, null, false);
        break;

      case "del":
        if (payload === "keyboard") index--;
        if (!newTags[index]) break;

        const { onDeleteTag } = this.props;
        const canDeleteTag = await onDeleteTag?.(newTags[index]);
        if (onDeleteTag && !Boolean(canDeleteTag)) break;

        const attr = newTags[index].attr;
        if (attr && attr?.removeable === false) {
          break;
        }

        newTags.splice(index, 1);
        this.setTags(newTags, () => {
          this.setState({ curPosType: FocusPosType.INPUT });
        });

        if (payload !== "edit") {
          this.setState({ showSelect: false });
        }
        break;

      case "click":
        if (!this.state.active) {
          this.open();
          return;
        }

        const pos = payload;
        // @ts-ignore
        newTags[index]["_edit"] = true;
        this.setTags(
          newTags,
          () => {
            this.setState({ showSelect: true }, () => {
              this.tagRefs[`tag-${index}`]?.edit(pos);
            });
          },
          false
        );
        this.setState({ curPosType: FocusPosType.INPUT_EDIT });
        break;

      case "click-input":
        if (payload === "edit") {
          this.setState({ curPosType: FocusPosType.INPUT_EDIT });
        } else {
          this.setState({ curPosType: FocusPosType.INPUT });
        }

        if (!this.state.active) {
          this.setState({ active: true });
        }

        this.setState({ showSelect: true });
        break;
    }

    this.setState({ curPos: index });
  };

  render() {
    const {
      attributes = [],
      hideHelp,
      tips = 'Separate keywords with "|"; press Enter to separate filter tags',
      attributesSelectTips = "Select a filter",
      disableAttributesFilter,
      disabled,
      forwardRef,
    } = this.props;

    const { active, curPos, curPosType, showSelect, tags } = this.state;

    // Áî®‰∫éËÆ°ÁÆó focused Âèä isFocused, Âà§Êñ≠ÊòØÂê¶ÊòæÁ§∫ÈÄâÊã©ÁªÑ‰ª∂
    let focusedInputIndex = -1;
    if (
      curPosType === FocusPosType.INPUT ||
      curPosType === FocusPosType.INPUT_EDIT
    ) {
      focusedInputIndex = curPos;
    }

    // Ê∏≤ÊüìÊ†áÁ≠æÂàóË°®
    const tagList = tags.map((item, index) => {
      // Ë°•ÂÖ® attr Â±ûÊÄß
      attributes.forEach((attrItem) => {
        if (item.attr && attrItem.key && attrItem.key == item.attr.key) {
          item.attr = Object.assign({}, item.attr, attrItem);
        }
      });

      const selectedAttrKeys: string[] = [];
      tags.forEach((tag) => {
        if (
          tag.attr &&
          item.attr &&
          // @ts-ignore
          item["_edit"] &&
          item.attr.key === tag.attr.key
        ) {
          return null;
        }
        if (tag.attr && tag.attr.key && !tag.attr.reusable) {
          selectedAttrKeys.push(tag.attr.key);
        }
      });

      const useableAttributes = attributes.filter(
        (item) => selectedAttrKeys.indexOf(item.key) < 0
      );

      return (
        <Tag
          ref={(tag) => (this.tagRefs[`tag-${index}`] = tag) as any}
          active={active}
          // @ts-ignore
          key={item["_key"]}
          attributes={useableAttributes}
          attr={item.attr}
          values={item.values}
          maxWidth={
            this.searchWrapRef.current
              ? this.searchWrapRef.current.clientWidth
              : null
          }
          focused={
            focusedInputIndex === index && showSelect ? curPosType : null
          }
          dispatchTagEvent={(type, payload) =>
            this.handleTagEvent(type, index, payload)
          }
        />
      );
    });

    // ËøáÊª§ÂèØÁî®Â±ûÊÄß
    const selectedAttrKeys = tags
      .map((item) => (item.attr && !item.attr.reusable ? item.attr.key : null))
      .filter((item) => !!item);

    const useableAttributes = attributes.filter(
      (item) => selectedAttrKeys.indexOf(item.key) < 0
    );

    // Ê∑ªÂä†ËæìÂÖ•Ê†áÁ≠æ
    tagList.push(
      <TagInput
        key="__input__"
        ref={(input) => (this.tagRefs[`tag-${tags.length}`] = input) as any}
        active={active}
        maxWidth={
          this.searchWrapRef.current
            ? this.searchWrapRef.current.clientWidth
            : null
        }
        attributes={useableAttributes}
        isFocused={focusedInputIndex === tags.length && showSelect}
        dispatchTagEvent={(type, payload) =>
          this.handleTagEvent(type, tags.length, payload)
        }
      />
    );

    return (
      <div className="w-full">
        <div
          className={cn(
            "flex w-full flex-wrap gap-2",
            "rounded-md border border-input",
            "pl-2 py-0",
            "bg-background text-sm",
            "disabled:cursor-not-allowed disabled:opacity-50",
            !active && "overflow-hidden",
            !disabled && [
              "cursor-text",
              active && "border-primary",
              active && "ring-4 ring-primary/20",
            ]
          )}
          ref={mergeRefs(this.searchWrapRef, forwardRef)}
        >
          <div
            className={cn(
              "flex flex-1 flex-wrap gap-x-1.5 gap-y-1",
              "items-center"
            )}
            ref={mergeRefs(this.searchBoxRef)}
            onClick={this.open}
          >
            <TagSearchBoxContext.Provider
              value={{
                attributesSelectTips,
                disableAttributesFilter,
                close: this.close,
              }}
            >
              <React.Fragment>
                {tagList}
                <div
                  className={cn(
                    "pointer-events-none text-muted-foreground/70 text-sm whitespace-nowrap flex items-center"
                  )}
                >
                  {tips}
                </div>
              </React.Fragment>
            </TagSearchBoxContext.Provider>
          </div>

          <div className={cn("flex items-center gap-0.5", "bg-background")}>
            {!!active && tags.length > 0 && (
              <TooltipProvider>
                <Tooltip>
                  <TooltipTrigger asChild>
                    <Button
                      variant="ghost"
                      size="icon"
                      className="h-[30px] w-[30px] p-0 hover:bg-muted rounded-none"
                      onClick={this.handleClear}
                    >
                      <X className="h-4 w-4" />
                      <span className="sr-only">Clear</span>
                    </Button>
                  </TooltipTrigger>
                  <TooltipContent side="bottom" className="text-xs">
                    Clear all tags
                  </TooltipContent>
                </Tooltip>
              </TooltipProvider>
            )}

            {!!active && !hideHelp && (
              <TooltipProvider>
                <Tooltip>
                  <TooltipTrigger asChild>
                    <Button
                      variant="ghost"
                      size="icon"
                      className="h-[30px] w-[30px] p-0 hover:bg-muted rounded-none"
                      onClick={this.handleHelp}
                    >
                      <Info className="h-4 w-4" />
                      <span className="sr-only">Help</span>
                    </Button>
                  </TooltipTrigger>
                  <TooltipContent side="bottom" className="text-xs">
                    Show help
                  </TooltipContent>
                </Tooltip>
              </TooltipProvider>
            )}

            <TooltipProvider>
              <Tooltip>
                <TooltipTrigger asChild>
                  <Button
                    variant="ghost"
                    size="icon"
                    className={cn(
                      "h-[30px] w-[30px] p-0 hover:bg-muted rounded-none rounded-r-md",
                      active && "text-primary"
                    )}
                    disabled={disabled}
                    onClick={this.handleSearch}
                  >
                    <Search className="h-4 w-4" />
                    <span className="sr-only">Search</span>
                  </Button>
                </TooltipTrigger>
                {active && (
                  <TooltipContent side="bottom" className="text-xs">
                    Search
                  </TooltipContent>
                )}
              </Tooltip>
            </TooltipProvider>
          </div>
        </div>

        <Dialog>
          <DialogContent className="max-w-[600px]">
            <DialogHeader>
              <DialogTitle className="text-base">Help</DialogTitle>
            </DialogHeader>
            {/* <TagSearchBoxHelp /> */}
          </DialogContent>
        </Dialog>
      </div>
    );
  }
}

export const TagSearchBoxWithOutsideClick = withOutsideClick("close")(
  ITagSearchBox,
  {
    ignoreClasses: ["ignore-outside-click"],
  }
);

export const TagSearchBox = forwardRef<HTMLDivElement, TagSearchBoxProps>(
  (props, ref) => {
    return <TagSearchBoxWithOutsideClick {...props} forwardRef={ref} />;
  }
);

TagSearchBox.displayName = "TagSearchBox";

import React, { Component, createRef } from "react";
import {
  AttributeSelect,
  AttributeValue,
} from "@/registry/new-york/tag-search-box/AttributeSelect.tsx";
import { ValueSelect } from "@/registry/new-york/tag-search-box/valueselect/ValueSelect.tsx";
import { TagSearchBoxContext } from "@/registry/new-york/tag-search-box/TagSearchboxContext.ts";
import { cn } from "@/lib/utils";

import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";

const keys: Record<string | number, string> = {
  8: "backspace",
  9: "tab",
  13: "enter",
  27: "esc",
  37: "left",
  38: "up",
  39: "right",
  40: "down",
};

const INPUT_MIN_SIZE = 0;
const SELECT_MIN_HEIGHT = 242;

interface TagInputProps {
  /**
   * Ëß¶ÂèëÊ†áÁ≠æÁõ∏ÂÖ≥‰∫ã‰ª∂
   */
  dispatchTagEvent: (type: string, payload?: any) => void;
  /**
   * ÊâÄÊúâÂ±ûÊÄßÈõÜÂêà
   */
  attributes: Array<AttributeValue>;
  /**
   * ÊòØÂê¶‰∏∫ Focus ÊÄÅ
   */
  isFocused: boolean;
  /**
   * ÊêúÁ¥¢Ê°ÜÊòØÂê¶Â§Ñ‰∫éÂ±ïÂºÄÁä∂ÊÄÅ
   */
  active: boolean;
  /**
   * ËæìÂÖ•Ê°ÜÁ±ªÂûãÔºàÁî®‰∫é‰øÆÊîπÊ†áÁ≠æÂÄºÁöÑ Input type ‰∏∫ "edit"Ôºâ
   */
  type?: "edit" | "add";
  /**
   * ÊòØÂê¶ÈöêËóè
   */
  hidden?: boolean;
  /**
   * ÊúÄÂ§ßÂÆΩÂ∫¶
   */
  maxWidth: number | null;
  /**
   * Â§ÑÁêÜÊåâÈîÆ‰∫ã‰ª∂
   */
  handleKeyDown?: (e: any) => void;
  /**
   * ‰ΩçÁΩÆÂÅèÁßª
   */
  inputOffset?: number;
}

interface TagInputState {
  inputWidth: number;
  inputValue: string;
  fullInputValue: string;
  attribute: AttributeValue | null;
  values: any[];
  showAttrSelect: boolean;
  showValueSelect: boolean;
  valueSelectOffset: number;
}

class TagInput extends Component<TagInputProps, TagInputState> {
  static contextType = TagSearchBoxContext;
  declare context: React.ContextType<typeof TagSearchBoxContext>;

  private wrapperRef = createRef<HTMLDivElement>();
  private inputRef = createRef<HTMLInputElement & HTMLTextAreaElement>();
  private inputMirrorRef = createRef<HTMLSpanElement>();
  private attrSelectRef = createRef<any>();
  private valueSelectRef = createRef<any>();

  constructor(props: TagInputProps) {
    super(props);
    this.state = {
      inputWidth: INPUT_MIN_SIZE,
      inputValue: "",
      fullInputValue: "",
      attribute: null,
      values: [],
      showAttrSelect: false,
      showValueSelect: false,
      valueSelectOffset: 0,
    };
  }

  // Helper function to get attribute string and value string
  private getAttrStrAndValueStr = (str: string) => {
    let attrStr = str,
      valueStr = "",
      pos = -1;

    for (let i = 0; i < this.props.attributes.length; ++i) {
      if (str.indexOf(this.props.attributes[i].name + ":") === 0) {
        attrStr = this.props.attributes[i].name;
        valueStr = str.substr(attrStr.length + 1);
        pos = this.props.attributes[i].name.length;
      }
    }

    return { attrStr, valueStr, pos };
  };

  // Refresh selection component display
  private refreshShow = () => {
    const { inputValue, attribute } = this.state;
    const input = this.inputRef.current;
    const start = input?.selectionStart ?? 0;
    const end = input?.selectionEnd ?? 0;
    const pos = this.getAttrStrAndValueStr(inputValue).pos;

    if (pos < 0 || start <= pos) {
      this.setState({
        showAttrSelect: true,
        showValueSelect: false,
      });
      return;
    }

    if (attribute && end > pos) {
      this.setState({
        showAttrSelect: false,
        showValueSelect: true,
      });
    }
  };

  // Focus input
  public focusInput = () => {
    if (!this.inputRef.current) return;
    this.inputRef.current.focus();
  };

  // Move cursor to end
  public moveToEnd = () => {
    const input = this.inputRef.current;
    input?.focus();
    const value = this.state.inputValue;

    setTimeout(() => input?.setSelectionRange(value.length, value.length), 0);
  };

  // Select value part
  public selectValue = () => {
    const input = this.inputRef.current;
    input?.focus();
    const value = this.state.inputValue;

    let pos = this.getAttrStrAndValueStr(value).pos;
    if (pos < 0) pos = -2;

    setTimeout(() => {
      input?.setSelectionRange(pos + 2, value.length);
      this.refreshShow();
    }, 0);
  };

  // Select attribute part
  public selectAttr = () => {
    const input = this.inputRef.current;
    input?.focus();
    const value = this.state.inputValue;
    let pos = this.getAttrStrAndValueStr(value).pos;
    if (pos < 0) pos = 0;

    setTimeout(() => {
      input?.setSelectionRange(0, pos);
      this.refreshShow();
    }, 0);
  };

  // Set input value
  public setInputValue = (value: string, callback?: () => void) => {
    if (this.props.type === "edit" && value.trim().length <= 0) {
      return this.props.dispatchTagEvent("del", "edit");
    }

    let attribute = null,
      valueStr = value;
    const mirror = this.inputMirrorRef.current;

    // Check if attribute exists
    for (let i = 0; i < this.props.attributes.length; ++i) {
      if (
        value.indexOf(this.props.attributes[i].name + ":") === 0 ||
        value.indexOf(this.props.attributes[i].name + "Ôºö") === 0
      ) {
        attribute = this.props.attributes[i];
        valueStr = value.substr(this.props.attributes[i].name.length + 1);

        if (mirror) {
          mirror.innerText = attribute.name + ": ";
          let width = mirror.clientWidth;
          if (this.props.inputOffset) width += this.props.inputOffset;
          this.setState({ valueSelectOffset: width });
        }
        break;
      }
    }

    // Handle leading spaces
    if (attribute && valueStr.replace(/^\s+/, "").length > 0) {
      value = attribute.name + ": " + valueStr.replace(/^\s+/, "");
    } else if (attribute) {
      value = attribute.name + ":" + valueStr;
    }

    if (attribute !== this.state.attribute) {
      this.setState({
        values: valueStr.split("|").map((item) => ({ name: item.trim() })),
      });
    }
    this.setState({ attribute }, this.refreshShow);

    if (this.props.type === "edit") {
      this.props.dispatchTagEvent("editing", { attr: attribute });
    }

    mirror!.innerText = value;
    const width = Math.max(mirror!.clientWidth, INPUT_MIN_SIZE);
    this.setState(
      {
        inputValue: value,
        fullInputValue: value,
        inputWidth: width,
      },
      () => {
        callback && callback();
      }
    );
  };

  // Set full input value (including IME process)
  private setFullInputValue = (value: string) => {
    let attribute = null,
      valueStr = value;
    const mirror = this.inputMirrorRef.current;

    for (let i = 0; i < this.props.attributes.length; ++i) {
      if (
        value.indexOf(this.props.attributes[i].name + ":") === 0 ||
        value.indexOf(this.props.attributes[i].name + "Ôºö") === 0
      ) {
        attribute = this.props.attributes[i];
        valueStr = value.substr(this.props.attributes[i].name.length + 1);

        if (mirror) {
          mirror.innerText = attribute.name + ": ";
          let width = mirror.clientWidth;
          if (this.props.inputOffset) width += this.props.inputOffset;
          this.setState({ valueSelectOffset: width });
        }
        break;
      }
    }

    if (attribute && valueStr.replace(/^\s+/, "").length > 0) {
      value = attribute.name + ": " + valueStr.replace(/^\s+/, "");
    } else if (attribute) {
      value = attribute.name + ":" + valueStr;
    }

    if (mirror) {
      mirror.innerText = value;
      const width = Math.max(mirror.clientWidth, INPUT_MIN_SIZE);
      this.setState({
        fullInputValue: value,
        inputWidth: width,
      });
    }
  };

  // Reset input
  public resetInput = (callback?: () => void) => {
    this.setInputValue("", callback);
    this.setState({ inputWidth: INPUT_MIN_SIZE });
  };

  // Get input value
  public getInputValue = () => {
    return this.state.inputValue;
  };

  // Add tag by input value
  public addTagByInputValue = () => {
    const { attribute, values, inputValue } = this.state;
    const type = this.props.type || "add";

    if (
      attribute &&
      this.props.attributes.filter((item) => item.key === attribute.key)
        .length > 0
    ) {
      if (values.length <= 0) {
        return false;
      }
      this.props.dispatchTagEvent(type, { attr: attribute, values });
    } else {
      if (inputValue.trim().length <= 0) {
        return false;
      }
      const list = inputValue
        .split("|")
        .filter((item) => item.trim().length > 0)
        .map((item) => ({ name: item.trim() }));
      this.props.dispatchTagEvent(type, { attr: null, values: list });
    }

    this.setState({
      showAttrSelect: false,
      showValueSelect: false,
    });

    if (this.props.type !== "edit") {
      this.resetInput();
    }
    return true;
  };

  // Event handlers
  private handleInputChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    this.setInputValue(e.target.value);
  };

  private handleInputClick = (e: React.MouseEvent) => {
    this.props.dispatchTagEvent("click-input", this.props.type);
    e.stopPropagation();
    this.focusInput();
  };

  private handleAttrSelect = (attr: any) => {
    if (attr && attr.key) {
      const str = attr.name + ": ";
      const { inputValue } = this.state;

      if (inputValue.indexOf(str) >= 0) {
        this.selectValue();
      } else {
        this.setInputValue(str);
      }
      this.setState({ values: [] });
    }
    this.focusInput();
  };

  private handleValueChange = (values: any[]) => {
    this.setState({ values });
    this.setInputValue(
      (this.state.attribute?.name ?? "") +
        ": " +
        values.map((item) => item.name).join(" | ")
    );
    this.focusInput();
  };

  private handleValueSelect = (values: any[]) => {
    this.setState({ values });

    if (values.length <= 0) {
      this.setInputValue((this.state.attribute?.name ?? "") + ": ");
      return;
    }

    if (values.length > 0 && this.state.attribute) {
      const key = this.state.attribute.key;
      if (this.props.attributes.filter((item) => item.key === key).length > 0) {
        const type = this.props.type || "add";
        this.props.dispatchTagEvent(type, {
          attr: this.state.attribute,
          values,
        });
      }
      this.focusInput();
    }

    if (this.props.type !== "edit") {
      this.resetInput();
    }
  };

  private handleValueCancel = () => {
    if (this.props.type === "edit") {
      const { attribute, values } = this.state;
      this.props.dispatchTagEvent("edit-cancel", {
        attr: attribute,
        values,
      });
    } else {
      this.resetInput(() => {
        this.focusInput();
      });
    }
  };

  private handlePaste = (e: React.ClipboardEvent) => {
    e.stopPropagation();
    e.preventDefault();

    const { attribute } = this.state;

    if (!attribute || attribute.type === "input") {
      let value = "";

      try {
        const clipboardData = e.clipboardData;
        value = clipboardData.getData("Text") || "";
      } catch (_) {}

      if (/^[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}/.test(value)) {
        value = value.replace(/[\r\n\t,Ôºå\s]+/g, "|");
      } else {
        value = value.replace(/[\r\n\t,Ôºå]+/g, "|");
      }

      value = value
        .split("|")
        .map((item) => item.trim())
        .filter((item) => item.length > 0)
        .join(" | ");

      const input = this.inputRef.current;
      const start = input?.selectionStart;
      const end = input?.selectionEnd;
      const { inputValue } = this.state;

      const curValue =
        inputValue.substring(0, start!) +
        value +
        inputValue.substring(end!, inputValue.length);

      if (attribute && attribute.type === "input") {
        this.setInputValue(curValue, this.focusInput);
        return;
      }

      if (inputValue.length > 0) {
        this.setInputValue(curValue, this.focusInput);
      } else {
        this.setInputValue(curValue, this.addTagByInputValue);
      }
    }
  };

  private handleKeyDown = (e: React.KeyboardEvent) => {
    if (!keys[e.keyCode]) return;

    if (this.props.hidden) {
      return this.props.handleKeyDown?.(e);
    }

    const { inputValue } = this.state;

    if (keys[e.keyCode] === "backspace" && inputValue.length > 0) return;

    if (
      (keys[e.keyCode] === "left" || keys[e.keyCode] === "right") &&
      inputValue.length > 0
    ) {
      setTimeout(this.refreshShow, 0);
      return;
    }

    if (keys[e.keyCode] === "esc") {
      if (!inputValue) {
        this.context.close?.();
      }
      return this.handleValueCancel();
    }

    e.preventDefault();

    if (this.attrSelectRef.current) {
      if (this.attrSelectRef.current.handleKeyDown(e.keyCode) === false) return;
    }

    if (this.valueSelectRef.current) {
      this.valueSelectRef.current.handleKeyDownForRenderMode(e.key);
      if (this.valueSelectRef.current.handleKeyDown(e.keyCode) === false)
        return;
    }

    switch (keys[e.keyCode]) {
      case "enter":
      case "tab":
        if (!this.props.isFocused) {
          this.props.dispatchTagEvent("click-input");
        }
        this.addTagByInputValue();
        break;
      case "backspace":
        this.props.dispatchTagEvent("del", "keyboard");
        break;
      case "up":
      case "down":
        break;
    }
  };

  public setInfo = (info: any, callback?: () => void) => {
    const attribute = info.attr;
    const values = info.values || [];

    this.setState({ attribute, values }, () => {
      if (attribute) {
        const valueStr = values.map((item: any) => item.name).join(" | ");
        this.setInputValue(attribute.name + ": " + valueStr, callback);
      } else {
        const valueStr = values.map((item: any) => item.name).join(" | ");
        this.setInputValue(valueStr, callback);
      }
    });
  };

  render() {
    const { active, hidden, maxWidth, type, isFocused, attributes } =
      this.props;

    const {
      inputWidth,
      inputValue,
      fullInputValue,
      showAttrSelect,
      showValueSelect,
      attribute,
      valueSelectOffset,
    } = this.state;
    const { valueStr, attrStr } = this.getAttrStrAndValueStr(inputValue);

    let maxHeight = SELECT_MIN_HEIGHT;
    try {
      if (this.wrapperRef.current) {
        maxHeight = Math.max(
          window.innerHeight -
            this.wrapperRef.current.getBoundingClientRect().bottom -
            60,
          SELECT_MIN_HEIGHT
        );
      }
    } catch (_) {}

    const isOpen =
      active &&
      isFocused &&
      (showAttrSelect || (showValueSelect && !!attribute && !!attribute.type));

    return (
      <div
        ref={this.wrapperRef}
        className={cn(
          "relative inline-flex items-center",
          "rounded-md",
          hidden && "hidden",
          type === "edit" && !hidden && "px-1"
        )}
        style={{
          width: hidden ? 0 : active ? inputWidth + 6 : 6,
        }}
        onClick={this.handleInputClick}
      >
        <Popover open={isOpen}>
          <PopoverTrigger asChild>
            <div
              className={cn("relative flex items-center")}
              style={{
                width: hidden ? 0 : inputWidth + 6,
                maxWidth: maxWidth ? maxWidth - 36 : 435,
              }}
            >
              {type !== "edit" ? (
                <Input
                  ref={this.inputRef as React.RefObject<HTMLInputElement>}
                  value={inputValue}
                  onChange={this.handleInputChange}
                  onKeyDown={this.handleKeyDown}
                  onClick={this.refreshShow}
                  onPaste={this.handlePaste}
                  onFocus={this.refreshShow}
                  onInput={(e) => this.setFullInputValue(e.currentTarget.value)}
                  className={cn(
                    "w-full border-none p-0 text-sm",
                    "bg-transparent",
                    "focus:outline-none focus:ring-0 focus-visible:ring-0",
                    "placeholder:text-muted-foreground/70",
                    "caret-foreground",
                    "shadow-none",
                    "flex items-center",
                    "resize-none",
                    "h-7"
                  )}
                  type="text"
                  style={{
                    width: hidden ? 0 : inputWidth + 6,
                    maxWidth: maxWidth ? maxWidth - 36 : 435,
                  }}
                  data-type="tag-input"
                />
              ) : (
                <React.Fragment>
                  <pre style={{ display: "block", visibility: "hidden" }}>
                    <div
                      className="text-sm"
                      style={{
                        width: hidden ? 0 : inputWidth + 36,
                        maxWidth: maxWidth ? maxWidth - 36 : 435,
                        whiteSpace: "normal",
                      }}
                    >
                      {fullInputValue}
                    </div>
                  </pre>
                  <Textarea
                    ref={this.inputRef as React.RefObject<HTMLTextAreaElement>}
                    value={inputValue}
                    onChange={this.handleInputChange}
                    onKeyDown={this.handleKeyDown}
                    onClick={this.refreshShow}
                    onPaste={this.handlePaste}
                    onFocus={this.refreshShow}
                    className={cn(
                      "w-full border-none p-0 text-sm min-h-none",
                      "bg-transparent",
                      "focus:outline-none focus:ring-0 focus-visible:ring-0",
                      "placeholder:text-muted-foreground/70",
                      "caret-foreground",
                      "shadow-none",
                      "resize-none",
                      "h-8"
                    )}
                    style={{
                      width: hidden ? 0 : inputWidth + 30,
                      maxWidth: maxWidth ? maxWidth - 36 : 435,
                      position: "absolute",
                      top: 0,
                      left: 0,
                      resize: "none",
                      overflow: "hidden",
                    }}
                  />
                </React.Fragment>
              )}
              <span
                ref={this.inputMirrorRef}
                className="invisible absolute whitespace-pre text-sm"
                style={{
                  pointerEvents: "none",
                  zIndex: -1,
                }}
              >
                {fullInputValue}
              </span>
            </div>
          </PopoverTrigger>
          <PopoverContent
            className={cn(
              "w-[--radix-popover-trigger-width] p-0",
              "rounded-md border bg-popover text-popover-foreground shadow-md",
              "ignore-outside-click"
            )}
            align="start"
            sideOffset={8}
            alignOffset={valueSelectOffset}
            onOpenAutoFocus={(e) => {
              e.preventDefault();
            }}
          >
            {showAttrSelect && (
              <AttributeSelect
                ref={this.attrSelectRef}
                attributes={attributes}
                inputValue={attrStr}
                maxHeight={maxHeight}
                onSelect={this.handleAttrSelect}
              />
            )}
            {showValueSelect && !!attribute && !!attribute.type && (
              <ValueSelect
                ref={this.valueSelectRef}
                type={attribute.type}
                values={attribute.values ?? []}
                render={attribute.render}
                inputValue={valueStr.trim()}
                offset={this.state.valueSelectOffset}
                onChange={this.handleValueChange}
                onSelect={this.handleValueSelect}
                onCancel={this.handleValueCancel}
                maxHeight={maxHeight}
              />
            )}
          </PopoverContent>
        </Popover>
      </div>
    );
  }
}

export { TagInput };

import React, { Component, createRef } from "react";
import { TagInput } from "@/registry/new-york/tag-search-box/TagInput";
import { FocusPosType } from "@/registry/new-york/tag-search-box/TagSearchBox";
import { X } from "lucide-react";
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import { cn } from "@/lib/utils";
import {
  Value,
  AttributeValue,
} from "@/registry/new-york/tag-search-box/AttributeSelect";

export interface TagValue {
  /**
   * Ê†áÁ≠æÂ±ûÊÄß
   */
  attr?: AttributeValue;
  /**
   * Ê†áÁ≠æÂ±ûÊÄßÂÄº
   */
  values?: Value[];
}

interface TagProps {
  /**
   * Ê†áÁ≠æÂ±ûÊÄß
   */
  attr?: AttributeValue;
  /**
   * Ê†áÁ≠æÂ±ûÊÄßÂÄº
   */
  values?: Value[];
  /**
   * Ëß¶ÂèëÊ†áÁ≠æÁõ∏ÂÖ≥‰∫ã‰ª∂
   */
  dispatchTagEvent?: (type: string, payload?: any) => void;
  /**
   * ÊâÄÊúâÂ±ûÊÄßÈõÜÂêà
   */
  attributes: AttributeValue[];
  /**
   * ÂΩìÂâçËÅöÁÑ¶Áä∂ÊÄÅ
   */
  focused: FocusPosType | null;
  /**
   * ÊúÄÂ§ßÈïøÂ∫¶
   */
  maxWidth?: number | null;
  /**
   * ÊêúÁ¥¢Ê°ÜÊòØÂê¶Â§Ñ‰∫éÂ±ïÂºÄÁä∂ÊÄÅ
   */
  active: boolean;
}

interface TagRef {
  focusTag: () => void;
  focusInput: () => void;
  resetInput: () => void;
  setInputValue: (value: string, callback?: () => void) => void;
  getInputValue: () => string | undefined;
  addTagByInputValue: () => boolean | undefined;
  addTagByEditInputValue: () => boolean | undefined;
  setInfo: (info: any, callback?: () => void) => void;
  moveToEnd: () => void;
  getInfo: any;
  edit: (pos: string) => void;
  editDone: () => void;
}

const keys: Record<string, string> = {
  "8": "backspace",
  "13": "enter",
  "37": "left",
  "38": "up",
  "39": "right",
  "40": "down",
};

export class Tag
  extends Component<TagProps, { inEditing: boolean }>
  implements TagRef
{
  private contentRef = createRef<HTMLDivElement>();
  private inputInsideRef: any = null;
  private inputRef: any = null;

  constructor(props: TagProps) {
    super(props);
    this.state = {
      inEditing: false,
    };
  }

  handleTagClick = (e: React.MouseEvent, pos?: string) => {
    e.stopPropagation();
    this.props.dispatchTagEvent?.("click", pos);
  };

  handleDelete = (e: React.MouseEvent) => {
    e.stopPropagation();
    this.props.dispatchTagEvent?.("del");
  };

  handleKeyDown = (e: React.KeyboardEvent) => {
    if (!keys[e.keyCode]) return;

    e.preventDefault();

    switch (keys[e.keyCode]) {
      case "tab":
      case "enter":
        this.props.dispatchTagEvent?.("click", "value");
        break;
      case "backspace":
        this.props.dispatchTagEvent?.("del", "keyboard");
        break;
    }
  };

  focusTag = () => {
    this.inputInsideRef?.focusInput();
  };

  focusInput = () => {
    this.inputRef?.focusInput();
  };

  resetInput = () => {
    this.inputInsideRef?.resetInput();
  };

  setInputValue = (value: string, callback?: () => void) => {
    this.inputRef?.setInputValue(value, callback);
  };

  getInputValue = () => {
    return this.inputRef?.getInputValue();
  };

  addTagByInputValue = () => {
    return this.inputRef?.addTagByInputValue();
  };

  addTagByEditInputValue = () => {
    if (!this.inputInsideRef) return;
    return this.inputInsideRef?.addTagByInputValue();
  };

  setInfo = (info: any, callback?: () => void) => {
    return this.inputRef?.setInfo(info, callback);
  };

  moveToEnd = () => {
    return this.inputRef?.moveToEnd();
  };

  getInfo = () => {
    const { attr, values } = this.props;
    const info = { attr, values };
    return info;
  };

  edit = (pos: string) => {
    this.setState({ inEditing: true });
    const input = this.inputInsideRef;
    input?.setInfo(this.getInfo(), () => {
      if (pos === "attr") {
        return input.selectAttr();
      }
      return input.selectValue();
    });
  };

  editDone = () => {
    this.setState({ inEditing: false });
  };

  render() {
    const {
      attr,
      values,
      dispatchTagEvent,
      attributes,
      focused,
      maxWidth,
      active,
    } = this.props;
    const { inEditing } = this.state;

    const formattedAttrStr = attr && attr.name ? `${attr.name}: ` : "";
    const valueStr = (values || []).map((item) => item.name).join(" | ");
    const removeable = attr && "removeable" in attr ? attr.removeable : true;

    return (
      <div
        onClick={(e) => this.handleTagClick(e)}
        tabIndex={0}
        role="button"
        ref={this.contentRef}
      >
        <div
          className={cn(
            "group relative inline-flex items-center gap-1",
            "rounded-md border border-input bg-background px-2 py-1",
            "text-sm transition-colors",
            "hover:bg-accent hover:text-accent-foreground",
            "cursor-text",
            "focus:outline-none focus:ring-1 focus:ring-ring focus:ring-offset-1"
          )}
          style={{
            display: inEditing ? "none" : undefined,
          }}
        >
          {attr && (
            <span
              className="text-muted-foreground/80 text-xs"
              onClick={(e) => {
                e.stopPropagation();
                this.handleTagClick(e, "attr");
              }}
            >
              {formattedAttrStr}
            </span>
          )}
          <span
            className="font-medium text-xs"
            onClick={(e) => {
              e.stopPropagation();
              this.handleTagClick(e, "value");
            }}
          >
            {valueStr}
          </span>
          {removeable && (
            <TooltipProvider>
              <Tooltip>
                <TooltipTrigger asChild>
                  <button
                    className={cn(
                      "ml-1 rounded-sm opacity-70 ring-offset-background",
                      "transition-opacity hover:opacity-100",
                      "focus:outline-none focus:ring-1 focus:ring-ring focus:ring-offset-1",
                      "disabled:pointer-events-none disabled:opacity-50"
                    )}
                    onClick={this.handleDelete}
                    disabled={!active}
                  >
                    <X className="h-3 w-3" />
                    <span className="sr-only">Remove tag</span>
                  </button>
                </TooltipTrigger>
                {active && (
                  <TooltipContent side="bottom" className="text-xs">
                    <p>Click to remove tag</p>
                  </TooltipContent>
                )}
              </Tooltip>
            </TooltipProvider>
          )}
        </div>

        {active && (
          <TooltipProvider>
            <Tooltip>
              <TooltipContent side="bottom" className="text-xs">
                <p>Click to modify. Press Enter to finish.</p>
              </TooltipContent>
            </Tooltip>
          </TooltipProvider>
        )}

        <TagInput
          type="edit"
          hidden={!inEditing}
          maxWidth={maxWidth!}
          handleKeyDown={this.handleKeyDown}
          active={active}
          ref={(input) => (this.inputInsideRef = input) as any}
          attributes={attributes}
          dispatchTagEvent={dispatchTagEvent!}
          isFocused={focused === FocusPosType.INPUT_EDIT}
        />
      </div>
    );
  }
}

