interface ValueSelectProps {
  /**
   * ÂÄºÈÄâÊã©ÁªÑ‰ª∂Á±ªÂûãÔºåÁî®‰∫éÈÄâÊã©‰∏çÂêåÁªÑ‰ª∂
   */
  type: AttributeValue["type"];
  /**
   * ÂÄºÈÄâÊã©ÁªÑ‰ª∂ÂèØÈÄâÂÄºÁöÑÈõÜÂêà
   */
  values?: AttributeValue["values"];
  /**
   * ÂΩìÂâçËæìÂÖ•ÂÄº
   */
  inputValue: string;
  /**
   * Ëá™ÂÆö‰πâÊ∏≤Êüì
   */
  render?: AttributeValue["render"];
  onChange?: (value: Value[]) => void;
  onSelect?: (value: Value[]) => void;
  onCancel?: () => void;
  offset: number;
  maxHeight: number;
}

interface ValueSelectRef {
  handleKeyDown: (keyCode: string | number) => boolean | void;
  handleKeyDownForRenderMode: (operationalKey: string) => boolean;
}

interface ValueSelectState {
  values: any[];
}

export class ValueSelect extends Component<
  ValueSelectProps & {
    forwardRef?: React.Ref<ValueSelectRef>;
  },
  ValueSelectState
> {
  private mount: boolean = false;
  private select: any = null;
  private operationalKeyDownListener: (key: string) => void = () => {};

  constructor(props: ValueSelectProps) {
    super(props);
    this.state = {
      values: Array.isArray(props.values) ? props.values : [],
    };
  }

  componentDidMount() {
    this.mount = true;
    const { values: propsValues } = this.props;

    if (typeof propsValues === "function") {
      const result = propsValues();

      // PromiseÂ§ÑÁêÜ
      if (result && "then" in result) {
        result.then((fetchedValues) => {
          if (this.mount) {
            this.setState({ values: fetchedValues });
          }
        });
      } else {
        if (this.mount) {
          this.setState({ values: result });
        }
      }
    }
  }

  componentWillUnmount() {
    this.mount = false;
  }

  handleKeyDown = (keyCode: string | number) => {
    if (this.select && this.select.handleKeyDown) {
      return this.select.handleKeyDown(keyCode);
    }
    return true;
  };

  handleKeyDownForRenderMode = (operationalKey: string) => {
    if (this.props.render) {
      this.operationalKeyDownListener(operationalKey);
    }
    return true;
  };

  render() {
    const { values } = this.state;
    const {
      type,
      inputValue,
      onChange,
      onSelect,
      onCancel,
      offset = 0,
      maxHeight,
      render,
    } = this.props;

    // Â¶ÇÊûúÊèê‰æõ‰∫ÜËá™ÂÆö‰πâÊ∏≤ÊüìÂáΩÊï∞
    if (render) {
      return (
        <DropdownMenu>
          {render({
            onOperationalKeyDown: (listener) => {
              this.operationalKeyDownListener = listener as any;
            },
            inputValue,
            onSelect: onSelect!,
            onCancel: onCancel!,
          })}
        </DropdownMenu>
      );
    }

    // Â§ÑÁêÜÁªÑ‰ª∂Á±ªÂûã
    let typeOptions: [string, any];

    if (Array.isArray(type)) {
      typeOptions = type;
    } else {
      typeOptions = [type, {}];
    }

    const commonProps = {
      values,
      inputValue,
      onChange,
      onSelect,
      onCancel,
      offset,
      maxHeight,
    };

    // Ê†πÊçÆ‰∏çÂêåÁ±ªÂûãÊ∏≤Êüì‰∏çÂêåÁªÑ‰ª∂
    switch (typeOptions[0]) {
      case "input":
        // PureInput requires all callbacks to be non-optional
        const inputProps = {
          values,
          inputValue: inputValue || "",
          onChange: onChange || (() => {}),
          onSelect: onSelect || (() => {}),
          onCancel: onCancel || (() => {}),
          offset,
          maxHeight,
        };
        return (
          <PureInput
            ref={(select) => (this.select = select) as any}
            {...inputProps}
          />
        );

      case "single":
        const singleOptions = typeOptions[1];

        if (!Array.isArray(values)) {
          return <Loading offset={offset} />;
        }

        if (!values.length) {
          return (
            <Empty {...singleOptions} offset={offset} onCancel={onCancel} />
          );
        }

        return (
          <SingleValueSelect
            {...commonProps}
            {...singleOptions}
            ref={(select) => (this.select = select)}
          />
        );

      case "multiple":
        const multipleOptions = typeOptions[1];

        if (!Array.isArray(values)) {
          return <Loading offset={offset} />;
        }

        if (!values.length) {
          return (
            <Empty {...multipleOptions} offset={offset} onCancel={onCancel} />
          );
        }

        return (
          <MultipleValueSelect
            {...commonProps}
            {...multipleOptions}
            ref={(select) => (this.select = select)}
          />
        );

      default:
        return null;
    }
  }
}

import { useEffect, forwardRef, useImperativeHandle } from "react";

const keys: Record<string, string> = {
  "9": "tab",
  "13": "enter",
};

const getValue = (value: string) => {
  return value.split("|").map((item) => ({
    name: item.trim(),
  }));
};

export interface PureInputProps {
  inputValue: string;
  onChange: (value: any[]) => void;
  onSelect: (value: any[]) => void;
}

interface PureInputRef {
  handleKeyDown: (keyCode: string) => boolean;
}

export const PureInput = forwardRef<PureInputRef, PureInputProps>(
  ({ onChange, inputValue, onSelect }, ref) => {
    useEffect(() => {
      // ÁºñËæëÂÆåÊàêÊó∂ inputValue Ë¢´ÁΩÆÁ©∫ÔºåÊ≠§Êó∂ onChange ‰ºöÂØºËá¥Ê†áÁ≠æÈîÆÂØªÊâæÂ§±Ë¥•
      if (inputValue.trim()) {
        onChange(getValue(inputValue));
      }
    }, [inputValue, onChange]);

    const handleKeyDown = (keyCode: string) => {
      if (!keys[keyCode]) {
        return false;
      }

      switch (keys[keyCode]) {
        case "tab":
        case "enter":
          if (inputValue.length <= 0) return false;

          if (onSelect) {
            onSelect(getValue(inputValue).filter((i) => !!i.name));
          }
          return false;
      }
      return false;
    };

    useImperativeHandle(ref, () => ({
      handleKeyDown,
    }));

    return null;
  }
);

PureInput.displayName = "PureInput";

import React, { Component } from "react";
